import React, { Component } from 'react';

//import MenuPopup from "reactjs-popup";
//import logo from './logo.svg';
import './Home.css';

function Event(props){
  return(
    <li>
      <div className="Event">
        <p id='eventName'>{props.name}</p>
        <p id='eventDesc'>{props.description}</p>
        <p id='eventDate'>{props.date}</p>
        <p id='eventTime'>{props.time}</p>
        <button id='edit'>Edit</button>
        <button id='delete'>Delete</button>
      </div>
    </li>
  );
}

let myEvent ={
  eventID: 1,
  name: 'Do Homework',
  description:'CSE 111',
  date: '12/03/2018',
  time: '18:00',
};

let myEvent2 ={
  eventID: 2,
  name: 'Do Project',
  description:'CSE 111',
  date: '12/03/2018',
  time: '20:00',
};

function EventList(props){
  let events =[];
  events.push(myEvent);
  events.push(myEvent2);

  const listEvents = events.map((event) => 
    <Event key = {event.eventID.toString()}
           name = {event.name}
           description = {event.description}
           date = {event.date}
           time = {event.time}
    />
  );

  return (
    <div class = 'EventContainer'>
      <ul class = 'EventList'>
        {listEvents}
      </ul>
    </div>
    
  );
}

function Popup(props){ 

  let state = {
    staticEvent:{
      userID: null,
    }
  }

  if(!props.active){
    return(null);
  }else return(
    <div id = 'createEventTypeWindow'>
      <button id = 'createStaticEvent'>Create Static Event</button>
      <br/>
      <button id = 'createFlexEvent'>Create Flex Event</button>
    </div>
  );
  
}

class Home extends Component {

  constructor(){
    super();
    this.state = {popupActive : false}
  }

  toggleCreateEvent = () => {
      this.setState({popupActive: !this.state.popupActive});
  }
 
  createStaticEvent = async e =>{
    e.preventDefault();
    const response = await fetch('/api/addEvent', {
      method: 'POST',
      headers:{
        'Content-Type': 'application/json',
      },
      body:JSON.stringify({}),
    });
  }


  render() {
    return (
        <div className= 'Home'>
          <header className="Header">
            <p>
              Scheduler Project 
            </p>
          </header>
          <EventList/>
          <Popup active = {this.state.popupActive}/>
          <button id='createEvent' onClick={this.toggleCreateEvent}>Create Event</button>
        </div>
    );
  }
}
export default Home;
