import React, { Component } from 'react';

//import MenuPopup from "reactjs-popup";
//import logo from './logo.svg';
import './Home.css';

function Event(props){
  return(
    <li>
      <div className="Event">
        <p id='eventName'>{props.name}</p>
        <p id='eventDesc'>{props.description}</p>
        <p id='eventDate'>{props.date}</p>
        <p id='eventTime'>{props.time}</p>
        <button id='edit'>Edit</button>
        <button id='delete'>Delete</button>
      </div>
    </li>
  );
}

let myEvent ={
  eventID: 1,
  name: 'Do Homework',
  description:'CSE 111',
  date: '12/03/2018',
  time: '18:00',
};

let myEvent2 ={
  eventID: 2,
  name: 'Do Project',
  description:'CSE 111',
  date: '12/03/2018',
  time: '20:00',
};

function EventList(props){
  let events =[];
  events.push(myEvent);
  events.push(myEvent2);

  const listEvents = events.map((event) => 
    <Event key = {event.eventID.toString()}
           name = {event.name}
           description = {event.description}
           date = {event.date}
           time = {event.time}
    />
  );

  return (
    <div class = 'EventContainer'>
      <ul class = 'EventList'>
        {listEvents}
      </ul>
    </div>
    
  );
}

function Popup(props){
  let state = {
    isHidden:true,
  };
  let toggleHidden = () => {
    state.isHidden = !state.isHidden;
  };
  
  Popup.toggleHidden = toggleHidden;

  if(state.isHidden){
    return(null);
  }else return(
    <div id = 'createEventTypeWindow'>
      <button id = 'createStaticEvent'>Create Static Event</button>
      <button id = 'createFlexEvent'>Create Static Event</button>
    </div>
  );
  
}

class Home extends Component {

 
  render() {
    return (
        <div className= 'Home'>
          <header className="Header">
            <p>
              Scheduler Project 
            </p>
          </header>
          <EventList/>
          <button id='createEvent' onClick={Popup.toggleHidden()}>Create Event</button>
          <Popup/>
        </div>
    );
  }
}
export default Home;
